mapper.select:
SELECT count(1) FROM orders o
LEFT JOIN order_statuses os on o.id = os.order_id
WHERE o.user_id = #{userId,jdbcType=VARCHAR} AND os.order_status = #{orderStatus,jdbcType=INTEGER}

    and o.is_comment = #{isComment,jdbcType=INTEGER}

SELECT os.order_id as orderId, os.order_status as orderStatus, os.created_time as createdTime, os.pay_time as payTime, os.deliver_time as deliverTime, os.success_time as successTime, os.close_time as closeTime, os.comment_time as commentTime FROM orders o LEFT JOIN order_statuses os on o.id = os.order_id WHERE o.user_id = #{userId,jdbcType=VARCHAR}
AND o.is_delete = #{isDelete,jdbcType=INTEGER}
AND os.order_status IN
#{orderStatus}
ORDER BY os.order_id DESC
